const navToggle=document.querySelector("#nav-toggle"),overlays=document.querySelector(".project-overlay, .schedule-overlay"),body=document.querySelector("body, main"),nav=document.querySelector("#sidebar-nav"),navLinks=document.querySelectorAll("#sidebar-nav .nav-link");navToggle.addEventListener("click",e=>{document.querySelectorAll("#nav-toggle, #sidebar-nav, main,body").forEach(el=>{el.classList.toggle("active"),nav.classList.contains("active")?navLinks.forEach((link,i)=>{setTimeout(e=>{link.classList.add("active")},100*(i+1))}):navLinks.forEach(link=>{link.classList.remove("active")})})});