function getParams(name){return new Proxy(new URLSearchParams(window.location.search),{get:(searchParams,prop)=>searchParams.get(prop)})[name]}const toggleLink=document.querySelectorAll(".toggle-link");toggleLink.forEach(el=>{el.addEventListener("click",(function(event){event.preventDefault(),this.classList.toggle("toggle-open");const toggleBox=this.nextElementSibling;if(toggleBox.classList.contains("active"))toggleBox.style.height="0px",toggleBox.addEventListener("transitionend",event=>{toggleBox.classList.remove("active")},{once:!0});else{toggleBox.classList.add("active"),toggleBox.style.height="auto";const height=toggleBox.clientHeight+"px";toggleBox.style.height="0px",setTimeout((function(){toggleBox.style.height=height}),0)}}))});